mapper.select:
SELECT idKey, groupIdKey, fileName, fileSize, downCount, 
	   serverFilePath
  FROM CM_Attachment
 WHERE idKey  >  0

   AND idKey = #{idKey}


   AND groupIdKey = #{groupIdKey}

mapper.insert:
INSERT INTO CM_AttachmentGroup (
	groupFileCount, userIdKey
) VALUES (
	#{groupFileCount}, #{userIdKey}
)

INSERT INTO CM_Attachment (
	groupIdKey, fileName, fileSize, downCount, serverFilePath
) VALUES (
	#{groupIdKey}, #{fileName}, #{fileSize}, #{downCount}, #{serverFilePath}
)

mapper.update:
UPDATE CM_AttachmentGroup SET
	   groupFileCount = #{groupFileCount},
	   userIdKey = #{userIdKey}
 WHERE idKey = #{idKey}

UPDATE CM_Attachment SET
	   downCount = #{downCount}
 WHERE idKey = #{idKey}
